import React, { Component } from 'react'
import './App.css'

import { sampleText } from './sampleText'


class App extends Component {
  state = {
    text: sampleText
  }
  render() {
  return (
    <div className="wrapper">
      <div className="colonne">
        <div>
          <textarea 
            className='textarea' 
            rows="35"
            value={ sampleText } />
        </div>
        <div className="result">
          <div> {sampleText} </div>
        </div>
      </div>
    </div>

  );
}
}

export default App;
